import React from "react";
import './sign-in.stlyes.scss';
import FormInput from '../form-input/form-input.component'

class SignIn extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            email: '' ,
            password: ''
        }
    }
    handleSubmit = event => {
        event.preventDefault();

        this.setState({
            email: '',
            password: ''
        })
    }
    handleChange = event => {
        const { value , name } = event.target;
        this.setState({[name]: value})
    }

    render() {
        return (
            <div className='sign-in'>
                <h2>I already have an account</h2>
                <span>Sign in with your email and password</span>
                <form onSubmit={this.handleSubmit}>
                    <input name="email" type = 'email' handleChange = {this.handleChange} value={this.state.email} required />
                    <label>Email</label>
                    <input name="password"  type = 'password' value={this.state.password} handleChange = {this.handleChange} required />
                    <label>Password</label>
                    <input type="submit" value="Submit Form" />
                </form>
            </div>
        )
    }
}


export default SignIn;
